SELECT (u.first_name+' '+u.midlle_name+' '+last_name) as full_name, u.phone, f.adress_town, f.education_level,f.do_you_work, f.job_title, f.cooking FROM Users u INNER JOIN FemaleInformation f ON u._id = f.user_id  WHERE u._id =(SELECT  female_id FROM MatchingMale  WHERE result='success' AND rank = (SELECT MAX(rank) FROM MatchingMale WHERE male_id =  1018)) OR u._id = (SELECT  female_id FROM MatchingFemale  WHERE result='success'AND rank = (SELECT MAX(rank) FROM MatchingFemale WHERE male_id =  1018) )
SELECT (u.first_name+' '+u.midlle_name+' '+last_name) as full_name, u.phone, m.adress_town, m.education_level, m.job_title, m.homework_help FROM Users u INNER JOIN MaleInformation m ON u._id = m.user_id  WHERE u._id =(SELECT  male_id FROM MatchingMale  WHERE result='success' AND  rank = (SELECT MAX(rank) FROM MatchingMale WHERE female_id = 1030)) OR u._id = (SELECT  male_id FROM MatchingFemale  WHERE result='success' AND  rank = (SELECT MAX(rank) FROM MatchingFemale WHERE female_id = 1030))